import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { ProductList } from './components/products/ProductList';
import { ProductForm } from './components/products/ProductForm';
import { HSCodeList } from './components/products/HSCodeList';
import { PackingListPage } from './components/packing/PackingListPage';
import { PackingListForm } from './components/packing/PackingListForm';
import { Login } from './components/auth/Login';
import { UserList } from './components/auth/UserList';
import { UserForm } from './components/auth/UserForm';
import { ProtectedRoute } from './components/auth/ProtectedRoute';
import { AuthProvider } from './context/AuthContext';
import { Layout } from './components/layout/Layout';

import 'react-toastify/dist/ReactToastify.css';

export default function App() {
  return (
    <AuthProvider>
      <BrowserRouter>
        <Routes>
          {/* Public routes */}
          <Route path="/login" element={<Login />} />
          <Route path="/" element={<Navigate to="/packing-list" replace />} />

          {/* Protected routes */}
          <Route element={<ProtectedRoute />}>
            <Route element={<Layout />}>
              <Route path="/packing-list" element={<PackingListPage />} />
              <Route path="/packing-list/new" element={<PackingListForm />} />
              <Route path="/packing-list/:id" element={<PackingListForm />} />

              <Route path="/products" element={<ProductList />} />
              <Route 
                path="/products/new" 
                element={<ProductForm onSubmit={async () => { window.location.href = '/products'; }} />} 
              />
              <Route 
                path="/products/:id" 
                element={<ProductForm onSubmit={async () => { window.location.href = '/products'; }} />} 
              />

              <Route 
                path="/hs-codes" 
                element={<HSCodeList onClose={() => {}} />} 
              />

              {/* Admin only routes */}
              <Route path="/users" element={<UserList />} />
              <Route path="/users/new" element={<UserForm />} />
              <Route path="/users/:id" element={<UserForm />} />
            </Route>
          </Route>
        </Routes>
        <ToastContainer />
      </BrowserRouter>
    </AuthProvider>
  );
}
