import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { useTheme } from '../../context/ThemeContext';
import type { Product } from '../../types/Product';
import { productService } from '../../services/api';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';

// Input alanlarÄ± iÃ§in ortak className'i bir deÄŸiÅŸkene alalÄ±m
const inputClassName = "mt-1 block w-full rounded-md border-gray-300 dark:border-gray-600 shadow-sm focus:border-blue-500 focus:ring-blue-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white";

const productSchema = yup.object().shape({
  name: yup.string().required('Product name is required'),
  sku: yup.string().required('SKU is required'),
  boxQuantity: yup.number()
    .required('Box quantity is required')
    .min(1, 'Quantity must be greater than 0'),
  boxDimensions: yup.object().shape({
    length: yup.number()
      .required('Length is required')
      .min(0.1, 'Length must be greater than 0'),
    width: yup.number()
      .required('Width is required')
      .min(0.1, 'Width must be greater than 0'),
    height: yup.number()
      .required('Height is required')
      .min(0.1, 'Height must be greater than 0')
  }),
  weights: yup.object().shape({
    net: yup.number()
      .required('Net weight is required')
      .min(0.1, 'Net weight must be greater than 0'),
    gross: yup.number()
      .required('Gross weight is required')
      .min(0.1, 'Gross weight must be greater than 0')
  })
});

interface ProductFormProps {
  initialData?: Product;
  onSubmit: (data: Product) => Promise<void>;
  submitButtonText?: string;
}

export const ProductForm: React.FC<ProductFormProps> = ({ 
  initialData, 
  onSubmit,
  submitButtonText = 'Save Product'
}) => {
  const { theme, toggleTheme } = useTheme();
  const { register, handleSubmit, formState: { errors }, reset } = useForm<Product>({
    defaultValues: initialData || {
      name: '',
      sku: '',
      boxQuantity: 1,
      boxDimensions: {
        length: 0.1,
        width: 0.1,
        height: 0.1
      },
      weights: {
        net: 0.1,
        gross: 0.1
      }
    },
    resolver: yupResolver(productSchema)
  });

  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleFormSubmit = async (data: Product) => {
    try {
      setIsSubmitting(true);
      await onSubmit(data);
      if (!initialData) {
        reset(); // Sadece yeni Ã¼rÃ¼n oluÅŸturulduÄŸunda formu temizle
      }
    } catch (error) {
      // Hata yÃ¶netimi Ã¼st bileÅŸende yapÄ±lÄ±yor
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white dark:bg-gray-800 rounded-lg shadow-md mt-8">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800 dark:text-white">Add New Product</h2>
        <button
          onClick={toggleTheme}
          className="px-4 py-2 rounded-md bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-white"
        >
          {theme === 'dark' ? 'ðŸŒž Light' : 'ðŸŒ™ Dark'}
        </button>
      </div>

      <form onSubmit={handleSubmit(handleFormSubmit)} className="space-y-6">
        <div className="grid grid-cols-2 gap-4">
          <div>
            <label 
              htmlFor="name"
              className="block text-sm font-medium text-gray-700 dark:text-gray-300"
            >
              Product Name
            </label>
            <input
              id="name"
              {...register('name', { required: 'Product name is required' })}
              className={inputClassName}
            />
            {errors.name && <p className="text-red-500 text-sm mt-1">{errors.name.message}</p>}
          </div>

          <div>
            <label 
              htmlFor="sku"
              className="block text-sm font-medium text-gray-700 dark:text-gray-300"
            >
              SKU
            </label>
            <input
              id="sku"
              {...register('sku', { required: 'SKU is required' })}
              className={inputClassName}
            />
            {errors.sku && <p className="text-red-500 text-sm mt-1">{errors.sku.message}</p>}
          </div>
        </div>

        <div>
          <h3 className="text-lg font-medium text-gray-800 dark:text-white mb-3">Box Details</h3>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label 
                htmlFor="boxQuantity"
                className="block text-sm font-medium text-gray-700 dark:text-gray-300"
              >
                Box Quantity
              </label>
              <input
                id="boxQuantity"
                type="number"
                {...register('boxQuantity', { 
                  required: 'Box quantity is required',
                  min: { value: 1, message: 'Quantity must be greater than 0' }
                })}
                className={inputClassName}
              />
              {errors.boxQuantity && <p className="text-red-500 text-sm mt-1">{errors.boxQuantity.message}</p>}
            </div>
          </div>

          <div className="grid grid-cols-3 gap-4 mt-4">
            <div>
              <label 
                htmlFor="length"
                className="block text-sm font-medium text-gray-700 dark:text-gray-300"
              >
                Length (cm)
              </label>
              <input
                id="length"
                type="number"
                {...register('boxDimensions.length', { required: 'Length is required' })}
                className={inputClassName}
              />
            </div>
            <div>
              <label 
                htmlFor="width"
                className="block text-sm font-medium text-gray-700 dark:text-gray-300"
              >
                Width (cm)
              </label>
              <input
                id="width"
                type="number"
                {...register('boxDimensions.width', { required: 'Width is required' })}
                className={inputClassName}
              />
            </div>
            <div>
              <label 
                htmlFor="height"
                className="block text-sm font-medium text-gray-700 dark:text-gray-300"
              >
                Height (cm)
              </label>
              <input
                id="height"
                type="number"
                {...register('boxDimensions.height', { required: 'Height is required' })}
                className={inputClassName}
              />
            </div>
          </div>
        </div>

        <div className="grid grid-cols-2 gap-4">
          <div>
            <label 
              htmlFor="netWeight"
              className="block text-sm font-medium text-gray-700 dark:text-gray-300"
            >
              Net Weight (kg)
            </label>
            <input
              id="netWeight"
              type="number"
              step="0.01"
              {...register('weights.net', { required: 'Net weight is required' })}
              className={inputClassName}
            />
          </div>
          <div>
            <label 
              htmlFor="grossWeight"
              className="block text-sm font-medium text-gray-700 dark:text-gray-300"
            >
              Gross Weight (kg)
            </label>
            <input
              id="grossWeight"
              type="number"
              step="0.01"
              {...register('weights.gross', { required: 'Gross weight is required' })}
              className={inputClassName}
            />
          </div>
        </div>

        <div className="flex justify-end space-x-4">
          <button
            type="button"
            className="px-4 py-2 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm text-sm font-medium text-gray-700 dark:text-gray-300 bg-white dark:bg-gray-700 hover:bg-gray-50 dark:hover:bg-gray-600"
          >
            Cancel
          </button>
          <button
            type="submit"
            disabled={isSubmitting}
            className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 dark:bg-blue-500 dark:hover:bg-blue-600 disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {isSubmitting ? (
              <div className="flex items-center">
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                Saving...
              </div>
            ) : (
              submitButtonText
            )}
          </button>
        </div>
      </form>
    </div>
  );
};
