import React, { useEffect, useState } from 'react';
import { productService } from '../../services/api';
import type { Product } from '../../types/Product';

export const ProductList: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const data = await productService.getAll();
        setProducts(data);
      } catch (err) {
        setError('Failed to fetch products');
      } finally {
        setIsLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (isLoading) {
    return (
      <div className="flex justify-center items-center h-48">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-red-500 text-center p-4">
        {error}
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto p-6">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800 dark:text-white">Products</h2>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {products.map((product) => (
          <div 
            key={product.id} 
            className="bg-white dark:bg-gray-800 rounded-lg shadow-md p-6 hover:shadow-lg transition-shadow"
          >
            <div className="flex justify-between items-start mb-4">
              <div>
                <h3 className="text-lg font-semibold text-gray-800 dark:text-white">{product.name}</h3>
                <p className="text-sm text-gray-500 dark:text-gray-400">SKU: {product.sku}</p>
              </div>
            </div>

            <div className="space-y-2">
              <p className="text-sm text-gray-600 dark:text-gray-300">
                Box Quantity: {product.boxQuantity}
              </p>
              <div className="text-sm text-gray-600 dark:text-gray-300">
                <p>Dimensions: {product.boxDimensions.length} × {product.boxDimensions.width} × {product.boxDimensions.height} cm</p>
              </div>
              <div className="text-sm text-gray-600 dark:text-gray-300">
                <p>Net Weight: {product.weights.net} kg</p>
                <p>Gross Weight: {product.weights.gross} kg</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};
