import React, { useState } from 'react';
import { ProductList } from './components/products/ProductList';
import { ThemeProvider } from './context/ThemeContext';
import { ToastContainer } from 'react-toastify';
import { Sidebar } from './components/layout/Sidebar';

export const App: React.FC = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  return (
    <ThemeProvider>
      <div className="min-h-screen bg-white dark:bg-gray-900 lg:flex">
        <Sidebar 
          isOpen={isSidebarOpen} 
          onToggle={() => setIsSidebarOpen(!isSidebarOpen)} 
        />

        {/* Main Content */}
        <div className="flex-1">
          {/* Hamburger Menu - Mobile Only */}
          <button
            onClick={() => setIsSidebarOpen(true)}
            className="lg:hidden fixed top-4 left-4 z-10 p-2 rounded-md bg-white dark:bg-gray-800 shadow-lg"
          >
            <svg className="h-6 w-6 text-gray-600 dark:text-gray-300" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>

          <ProductList />
        </div>
      </div>
      <ToastContainer position="top-right" />
    </ThemeProvider>
  );
};
